import { useState } from "react";
import { Link } from "react-router-dom";

export default function SignUpPage() {
  const [user, setUser] = useState({
    email: '',
    password: '',
    firstName: '',
    lastName: '',
    phoneNumber: '',
  })

  function onHandleChange(e){
    const { name, value } = e.target
    setUser(prevUser => ({
        ...prevUser,
        [name]: value
    }))
  }

  function onHandleSubmit() {
    setUser({
        email: '',
        password: '',
        firstName: '',
        lastName: '',
        phoneNumber: '',
    })
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="p-14 bg-white rounded-lg shadow-xl space-y-6 max-w-2xl w-full mx-4"> 
        <label htmlFor="email" className="block text-sm font-semibold text-gray-700 mb-2">
          Enter your email
        </label>
        <input
          id="email"
          type="email"
          placeholder="Your Email"
          className="border-2 border-gray-300 bg-white h-10 px-5 rounded-lg text-sm focus:outline-none w-full"
          value={user.email}
          onChange={onHandleChange}
        />
        <label htmlFor="password" className="block text-sm font-semibold text-gray-700">
          Enter your password
        </label>
        <input
          id="password"
          type="password"
          placeholder="Your Password"
          className="border-2 border-gray-300 bg-white h-10 px-5 rounded-lg text-sm focus:outline-none w-full"
          value={user.password}
          onChange={onHandleChange}
        />
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded w-full"
          onClick={onHandleSubmit}
        >
          Log In
        </button>
        <div className="text-center text-gray-500">
          Already have an account yet? <Link to=".." relative="path" className="text-blue-500 hover:text-blue-700">Login in here</Link>
        </div>
      </div>
    </div>
  );
}
