import { useState } from "react";
import { NavLink } from "react-router-dom";

export default function ForgotPasswordPage() {
  const [email, setEmail] = useState("");

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="p-14 bg-white rounded-lg shadow-xl space-y-6 max-w-2xl w-full mx-4 space-x-2">
        <label
          htmlFor="email"
          className="block text-sm font-semibold text-gray-700"
        >
          Enter your email
        </label>
        <input
          id="email"
          placeholder="enter your email"
          className="border-2 border-gray-300 bg-gray-50 h-10 px-5 rounded-lg text-sm focus:outline-none w-full placeholder-gray-500 space-y-4"
          type="email"
          onChange={(event) => setEmail(event.target.value)}
          value={email}
        ></input>
        <NavLink to='login'>
        <button
          className="bg-blue-500 rounded-lg py-2 px-4 font-bold w-full text-white"
          type="submit"
        >

          NOOO
        </button>
        </NavLink>
      </div>
    </div>
  );
}
