import { useState } from "react";

export default function LoginPage() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  function onHandleSubmit() {
    setEmail("");
    setPassword("");
  }

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="p-12 bg-white rounded-lg shadow-xl space-y-6 space-x-5"> 
        <label
          htmlFor="email"
          className="block text-sm font-semibold text-gray-700 mb-2"
        >
          Enter your email
        </label>
        <input
          id="email"
          type="email"
          placeholder="Your Email"
          className="border-2 border-gray-300 bg-white h-10 px-5 rounded-lg text-sm focus:outline-none w-full"
          onChange={(event) => setEmail(event.target.value)}
          value={email}
        />
        <label
          htmlFor="password"
          className="block text-sm font-semibold text-gray-700 mb-2"
        >
          Enter your password
        </label>
        <input
          id="password"
          type="password"
          placeholder="Your Password"
          className="border-2 border-gray-300 bg-white h-10 px-5 rounded-lg text-sm focus:outline-none w-full"
          onChange={(event) => setPassword(event.target.value)}
          value={password}
        />
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded w-full"
          onClick={onHandleSubmit}
        >
          Log In
        </button>
        <div className="text-center text-gray-500">Don't have an account yet?</div> {/* Added text-center for better alignment */}
        {/* Displaying email and password for demonstration, might want to remove in production */}
        <div className="text-center">
          {email} {password}
        </div>
      </div>
    </div>
  );
}
