import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

export default function RegisterPage() {
  const [user, setUser] = useState({
    email: "",
    password: "",
    firstName: "",
    lastName: "",
    phoneNumber: "",
    confirmedPassword: ""
  });
  const [passwordsMatch, setPasswordsMatch] = useState(true);

  useEffect(() => {
    // This effect runs whenever the password or confirmedPassword changes.
    const match = user.password === user.confirmedPassword || user.confirmedPassword.length === 0;
    setPasswordsMatch(match);
  }, [user.password, user.confirmedPassword]);

  function onHandleChange(e) {
    const { id, value } = e.target;
    setUser((prevUser) => ({
      ...prevUser,
      [id]: value,
    }));
  }

  function onHandleSubmit(e) {
    e.preventDefault(); // Prevent default form submission behavior
    // Submit logic here
  }

  return (
    <form className="flex items-center justify-center min-h-screen bg-gray-100" onSubmit={onHandleSubmit}>
      <div className="p-14 bg-white rounded-lg shadow-xl space-y-6 max-w-2xl w-full mx-4">
        {/* Your input fields here */}

        <label htmlFor="confirmedPassword" className="block text-sm font-semibold text-gray-700">
          Confirm your password
        </label>
        <input
          id="confirmedPassword"
          type="password"
          placeholder="Confirm password"
          className={`border-2 ${!passwordsMatch && user.confirmedPassword.length > 0 ? "border-red-500" : "border-gray-300"} bg-white h-10 px-5 rounded-lg text-sm focus:outline-none w-full`}
          value={user.confirmedPassword}
          onChange={onHandleChange}
        />
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded w-full"
        >
          Register
        </button>
        <div className="text-center text-gray-500">
          Already have an account?{" "}
          <Link to=".." className="text-blue-500 hover:text-blue-700">
            Login here
          </Link>
        </div>
      </div>
    </form>
  );
}
